import React from "react";

////////////////////////////////////////////////////////////////////////////////
// Page Content
////////////////////////////////////////////////////////////////////////////////
export type Content = {
  using: IconChipItem[];
  interested: IconChipItem[];
  like: IconChipItem[];
  activities: TimelineItem[];
  socialLinks: SocialLinkItem[];
};

////////////////////////////////////////////////////////////////////////////////
// Social Links
////////////////////////////////////////////////////////////////////////////////
export type SocialLinkSite = "github" | "facebook" | "atcoder" | "note" | { sitename: string };

export type SocialLinkItem = {
  site: SocialLinkSite;
  username: string;
};

////////////////////////////////////////////////////////////////////////////////
// Timeline
////////////////////////////////////////////////////////////////////////////////
export type LinkItem = {
  text: string;
  url: string;
};

export type TimelineItem = {
  year: number;
  month: number;
  title: string;
  description?: string;
  links?: LinkItem[];
};

////////////////////////////////////////////////////////////////////////////////
// Icon Chips
////////////////////////////////////////////////////////////////////////////////
export type IconChipItem = {
  icon: React.ReactNode;
  text: string;
};
